package com.acadgild;

/**
 * 
 * Create a function which accepts two int type parameters and display their
 * sum.
 *
 */
public class Session4Assignment1_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Session4Assignment1_3.sum(4, 9);
	}

	public static void sum(int fNumber, int sNumber) {
		int sum;

		sum = fNumber + sNumber;

		System.out.println("First Number is:" + fNumber);
		System.out.println("");
		System.out.println("Second Number is:" + sNumber);
		System.out.println("");
		System.out.println("Sum is:" + sum);

	}

}
